import React, { Component } from 'react';

export default class Eight extends Component {

    constructor(props) {
        super(props);

        this.state = {
            bill: "",
            people: "",
            tip: ""
        }

    }

    render () {
        return (
            <div className="Eight" style={{background: 'lightgrey', borderStyle: 'solid', margin: '1rem', textAlign: 'center'}}>
                <h3>Tip Calculator</h3>
                <form>
                    <input
                    type="bill"
                    name="bill"
                    placeholder="How Much Was Your Bill ?"
                    value={this.state.bill}
                    onChange={this.handleChange}
                    required></input><p></p>
                    <input
                    type="people"
                    name="people"
                    placeholder="How Many People Sharing The Bill ?"
                    value={this.state.people}
                    onChange={this.handleChange}
                    required></input><p></p>
                    <select id = "selColor">
                        <option value = "#FFFFFF">White</option>
                        <option value = "#FF0000">Red</option>
                        <option value = "#FFCC00">Orange</option>
                        <option value = "#FFFF00">Yellow</option>
                        <option value = "#00FF00">Green</option>
                        <option value = "#0000FF">Blue</option>
                        <option value = "#663366">Indigo</option>
                        <option value = "#FF00FF">Violet</option>
                    </select>
  How Was Your Service ?
                </form>
            </div>
        )
    }
}
